SELECT PIZZA_TYPES.CATEGORY,COUNT(ORDER_DETAILS.QUANTITY) TOTAL_ORDERS FROM PIZZA_TYPES INNER JOIN PIZZAS ON PIZZA_TYPES.PIZZA_TYPE_ID =PIZZAS.PIZZA_TYPE_ID INNER JOIN  ORDER_DETAILS ON ORDER_DETAILS.PIZZA_ID=PIZZAS.PIZZA_ID GROUP BY PIZZA_TYPES.CATEGORY;
SELECT TO_CHAR(TO_DATE(ORDER_TIME,'HH24:MI:SS'),'HH24') HOURS ,COUNT(ORDER_ID) TOTAL_ORDERS FROM ORDERS GROUP BY TO_CHAR(TO_DATE(ORDER_TIME,'HH24:MI:SS'),'HH24');
SELECT CATEGORY, COUNT(NAME) FROM PIZZA_TYPES GROUP BY CATEGORY;

SELECT ROUND(AVG(SUM_ORDERS),2) AVG_NO_OF_ORDERS_PER_DAY FROM ( SELECT TO_CHAR(ORDERS.ORDER_DATE,'DD') DAY,SUM(ORDER_DETAILS.QUANTITY) SUM_ORDERS FROM ORDERS INNER JOIN ORDER_DETAILS ON ORDERS.ORDER_ID=ORDER_DETAILS.ORDER_ID GROUP BY TO_CHAR(ORDERS.ORDER_DATE,'DD') );
SELECT PIZZA_TYPES.NAME PIZZA_NAME,SUM(ORDER_DETAILS.QUANTITY*PIZZAS.PRICE) TOTAL_REVENUE FROM PIZZA_TYPES INNER JOIN PIZZAS ON PIZZA_TYPES.PIZZA_TYPE_ID=PIZZAS.PIZZA_TYPE_ID INNER JOIN ORDER_DETAILS ON ORDER_DETAILS.PIZZA_ID=PIZZAS.PIZZA_ID GROUP BY PIZZA_TYPES.NAME;